import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { useWeb3React } from '@web3-react/core';

const DisplayBox = ({ connectWallet, active, balance, network }: any) => {
  const { account, library } = useWeb3React();
  const [networkName, setNetworkName] = useState(network);
  useEffect(() => {
    if (library !== undefined && library.network !== undefined) {
      setNetworkName(`${library.network.name} network`);
    }
  }, [account, library]);
  return (
    <div
      className="login100-more"
      style={{
        padding: '2em 1em 1em 1em',
      }}
    >
      <div className="container-fluid">
        <div className="row">
          {active ? (
            <div className="col-12">
              <div>
                <div className="bg1 p-l-5 p-t-10 user-account">
                  <img
                    className="ml-2"
                    src="data:image/png;base64,"
                    alt=""
                    width="30"
                    height="30"
                    style={{ marginTop: '0px' }}
                  />
                  <small
                    className="text-capitalize"
                    style={{ marginTop: '4px' }}
                  >
                    {account}
                  </small>
                  <small
                    className="text-capitalize"
                    style={{ marginTop: '4px' }}
                  >
                    {balance} ETH
                  </small>
                  <small
                    className="text-capitalize"
                    style={{ marginTop: '4px' }}
                  >
                    {networkName}
                  </small>
                </div>
              </div>
            </div>
          ) : (
            <div className="col pull-right">
              <button
                type="button"
                style={{ float: 'inline-end', color: 'white !important' }}
                className="btn btn-success btn-sm"
                onClick={connectWallet}
              >
                Connect Wallet
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
DisplayBox.propTypes = {
  connectWallet: PropTypes.func.isRequired,
  active: PropTypes.bool.isRequired,
  balance: PropTypes.string.isRequired,
  network: PropTypes.string.isRequired,
};
export default DisplayBox;
